def main_00():
    p_00 = 'a_'
    a_00 = []

    for i in range(127 + 1):
        v_00 = str(i).zfill(3)
        v_01 = hex(i)[2:]
        v_02 = repr(chr(i))
        v_03 = '\\x' + v_01.zfill(2)
        v_04 = '\\u' + v_01.zfill(4)

        a_00.append(' '.join([
            p_00 + v_00, a_061, v_02.ljust(8, ' '), a_035, ' | '.join([v_03, v_04])
        ]))

        print(a_00[-1])

    storage('../string/ascii.py', a_00)


