inline uint RotateLeft(uint value, int shift) {
    return (value << shift) | (value >> (32 - shift));
}

inline byte RotateLeftB(byte value, int shift) {
    return (value << shift) | (value >> (8 - shift));
}

inline uint Pack(byte *b){
    uint temp = 0;
    for (int i = 0; i <= 3; i++)
        temp |= ((byte)b[i] << (i * 8));
    return temp;
}

inline uint PackC(const byte *b){
    uint temp = 0;
    for (int i = 0; i <= 3; i++)
        temp |= ((byte)b[i] << (i * 8));
    return temp;
}

inline uint PackFrom(byte *b, int k){
    uint temp = 0;
    for (byte i = 0; i <= 3; i++)
        temp |= ((uint)b[i + k] << (i * 8));
    return temp;
}

inline void Unpack(uint a, byte *b){
    b[0] = (byte)a;
    b[1] = (byte)(a >> 8);
    b[2] = (byte)(a >> 16);
    b[3] = (byte)(a >> 24);
}

inline void UnpackFrom(uint a, byte *b, int k){
    b[0 + k] = (byte)a;
    b[1 + k] = (byte)(a >> 8);
    b[2 + k] = (byte)(a >> 16);
    b[3 + k] = (byte)(a >> 24);
}

static inline byte bmul(byte x, byte y){
    if (x != 0 && y != 0)
        return ptab[(ltab[x] + ltab[y]) % 255];
    else
        return 0;
}

static inline uint SubByte(uint a){
    byte b[4];
    Unpack(a, b);
    b[0] = fbsub[b[0]];
    b[1] = fbsub[b[1]];
    b[2] = fbsub[b[2]];
    b[3] = fbsub[b[3]];
    return Pack(b);
}

static inline byte product(uint x, uint y){
    byte xb[4];
    byte yb[4];
    Unpack(x, xb);
    Unpack(y, yb);
    return (byte)(bmul(xb[0], yb[0]) ^ bmul(xb[1], yb[1]) ^ bmul(xb[2], yb[2]) ^ bmul(xb[3], yb[3]));
}

static inline uint InvMixCol(uint x){
    uint y, m;
    byte b[4];
    m = PackC(InCo);
    b[3] = product(m, x);
    m = RotateLeft(m, 24);
    b[2] = product(m, x);
    m = RotateLeft(m, 24);
    b[1] = product(m, x);
    m = RotateLeft(m, 24);
    b[0] = product(m, x);
    y = Pack(b);
    return y;
}

static inline void gkey(int nb, int nk, byte *key){
    int i, j, k, m, N;
    int C1, C2, C3;
    uint CipherKey[8];

    Nb = nb;
    Nk = nk;

    if (Nb >= Nk)
        Nr = 6 + Nb;
    else
        Nr = 6 + Nk;

    C1 = 1;
    if (Nb < 8){
        C2 = 2;
        C3 = 3;
    } else {
        C2 = 3;
        C3 = 4;
    }

    for (m = j = 0; j < nb; j++, m += 3){
        fi[m] = (byte)((j + C1) % nb);
        fi[m + 1] = (byte)((j + C2) % nb);
        fi[m + 2] = (byte)((j + C3) % nb);

        ri[m] = (byte)((nb + j - C1) % nb);
        ri[m + 1] = (byte)((nb + j - C2) % nb);
        ri[m + 2] = (byte)((nb + j - C3) % nb);
    }

    N = Nb * (Nr + 1);

    for (i = j = 0; i < Nk; i++, j += 4)
        CipherKey[i] = PackFrom(key, j);

    for (i = 0; i < Nk; i++)
        fkey[i] = CipherKey[i];

    for (j = Nk, k = 0; j < N; j += Nk, k++){
        fkey[j] = fkey[j - Nk] ^ SubByte(RotateLeft(fkey[j - 1], 24)) ^ rco[k];

        if (Nk <= 6){
            for (i = 1; i < Nk && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];
        } else {
            for (i = 1; i < 4 && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];

            if ((j + 4) < N)
                fkey[j + 4] = fkey[j + 4 - Nk] ^ SubByte(fkey[j + 3]);

            for (i = 5; i < Nk && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];
        }
    }

    for (j = 0; j < Nb; j++)
        rkey[j + N - Nb] = fkey[j];

    for (i = Nb; i < (N - Nb); i += Nb){
        k = N - Nb - i;

        for (j = 0; j < Nb; j++)
            rkey[k + j] = InvMixCol(fkey[i + j]);

    }

    for (j = (N - Nb); j < N; j++)
        rkey[j - N + Nb] = fkey[j];
}

static inline void decrypt(byte *buff){
    int i, j, k, m;
    uint a[8];
    uint b[8];
    uint *x, *y, *t;

    for (i = j = 0; i < Nb; i++, j += 4){
        a[i] = PackFrom(buff, j);
        a[i] ^= rkey[i];
    }

    k = Nb;
    x = a;
    y = b;

    for (i = 1; i < Nr; i++){
        for (m = j = 0; j < Nb; j++, m += 3)
            y[j] = rkey[k++] ^ rtable[(byte)x[j]] ^
                RotateLeft(rtable[(byte)(x[ri[m]] >> 8)], 8) ^
                RotateLeft(rtable[(byte)(x[ri[m + 1]] >> 16)], 16) ^
                RotateLeft(rtable[x[ri[m + 2]] >> 24], 24);

        t = x;
        x = y;
        y = t;
    }

    for (m = j = 0; j < Nb; j++, m += 3)
        y[j] = rkey[k++] ^ (uint)rbsub[(byte)x[j]] ^
            RotateLeft((uint)rbsub[(byte)(x[ri[m]] >> 8)], 8) ^
            RotateLeft((uint)rbsub[(byte)(x[ri[m + 1]] >> 16)], 16) ^
            RotateLeft((uint)rbsub[x[ri[m + 2]] >> 24], 24);

    for (i = j = 0; i < Nb; i++, j += 4){
        UnpackFrom(y[i], buff, j);
        x[i] = y[i] = 0;
    }
}

static inline byte* decryptaes(byte *data, byte *key, byte *iv, int elen, int klen, int ilen, int blocksize, int keysize, int mode, int* declen) {
    int i, j, nb, nk, bcount, kcount;
    byte *decd;

    nb = (blocksize == 128) ? 4 : (blocksize == 192) ? 6 : 8;
    nk = (keysize == 128) ? 4 : (keysize == 192) ? 6 : 8;

    bcount = nb * 4;
    kcount = nk * 4;

    byte vectorBlock[8 * 4] = {0};
    byte vectorBlock2[8 * 4] = {0};
    byte keyBlock[8 * 4] = {0};

    memcpy(keyBlock, key, (klen < kcount) ? klen : kcount);
    memcpy(vectorBlock, iv, (ilen < bcount) ? ilen : bcount);

    gkey(nb, nk, keyBlock);

    if (mode == 0) {
        for (i = 0; i < elen; i += bcount) {
            memcpy(vectorBlock2, data + i, bcount);
            decrypt(data + i);
            for (j = 0; j < bcount; j++)
                data[i + j] ^= vectorBlock[j];
            memcpy(vectorBlock, vectorBlock2, bcount);
        }
    } else {
        for (i = 0; i < elen; i += bcount) {
            decrypt(data + i);
        }
    }

    byte last = data[elen - 1];
    int dlen = (elen <= last) ? elen : elen - last;
    *declen = dlen;

    decd = (byte*)malloc(dlen);
    memcpy(decd, data, dlen);

    return decd;
}

static PyObject* py_decrypt(PyObject* self, PyObject* args, PyObject *kwargs) {
    char *data;
    char *key;
    char *iv;
    Py_ssize_t dlen, klen, ilen;

    int block_size = 128;
    int key_size = 128;
    int mode = 0;

    static char *kwlist[] = {"data", "key", "iv", "blocksize", "keysize", "mode", NULL};

    if (!PyArg_ParseTupleAndKeywords(args, kwargs, "y#y#y#|iii", kwlist, &data, &dlen, &key, &klen,  &iv, &ilen, &block_size, &key_size, &mode)) {
        return NULL;
    }

    byte *darr = (byte*)data;
    byte *karr = (byte*)key;
    byte *iarr = (byte*)iv;

    int declen;
    byte* decd = decryptaes(darr, karr, iarr, (int)dlen, (int)klen, (int)ilen, block_size, key_size, mode, &declen);

    PyObject* result = PyBytes_FromStringAndSize((const char *)decd, declen);
    free(decd);

    return result;
}

static PyMethodDef CRijndaelMethods[] = {
    {"decrypt", (PyCFunction)py_decrypt, METH_VARARGS | METH_KEYWORDS, "Decrypt data"},
    {NULL, NULL, 0, NULL}
};

static struct PyModuleDef crijndaelmodule = {
    PyModuleDef_HEAD_INIT,
    "crijndael",
    NULL,
    -1,
    CRijndaelMethods
};

PyMODINIT_FUNC PyInit_crijndael(void) {
    return PyModule_Create(&crijndaelmodule);
}
